                queryBuilder.where('id').not().allOf('1', 2);
                queryBuilder.orderBy('id', 'asc');

                const result = await LogRepository.findOne(queryBuilder);
                expect(result).to.not.be.null;
                expect(result.id).to.equal(3);
            });
